INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (6198, '1956-09-30', null, null, 'Vondie Curtis-Hall', 'From Wikipedia, the free encyclopediaVondie Curtis-Hall (born September 30, 1956) is an American actor and film director.As an actor, he is best known for his role as Dr. Dennis Hancock on the CBS medical drama Chicago Hope created by David E. Kelley.Description above from the Wikipedia article Vondie Curtis-Hall, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Detroit, Michigan, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (6199, '1941-05-18', null, null, 'Miriam Margolyes', 'An English actress and voice artist. Her earliest roles were in theatre and after several supporting roles in film and television she won a BAFTA Award for her role in The Age of Innocence (1993).Description above from the Wikipedia article Miriam Margolyes, licensed under CC-BY-SA,full list of contributors on Wikipedia.', 'Oxford, England, UK');
