INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (6413, '1962-05-14', null, '丹尼·赫斯顿', 'Danny Huston', 'Daniel "Danny" Huston (born May 14, 1962) is an American actor and director.Description above from the Wikipedia article Danny Huston, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Rome, Italy');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (6486, '1940-07-24', null, null, 'Dan Hedaya', 'An American character actor. He often plays sleazy villains or uptight, wisecracking individuals; two of his best-known roles are as a cuckolded husband in the Coen brothers'' crime thriller Blood Simple, and the scheming Nick Tortelli on the sitcom Cheers.From Wikipedia, the free encyclopedia', 'Brooklyn, New York, USA');
