INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (7904, '1948-03-14', null, null, 'Billy Crystal', 'William Edward "Billy" Crystal (born March 14, 1948) is an American actor, writer, producer, comedian and film director. He gained prominence in the 1970s for playing Jodie Dallas on the ABC sitcom Soap and became a Hollywood film star during the late 1980s and 1990s, appearing in the critical and box office successes When Harry Met Sally... and City Slickers. He has hosted the Academy Awards eight times.', 'Long Beach, Long Island, New York, USA');
