INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (8552, '1988-11-09', null, null, 'Alexandrea Owens', 'Alexandrea Owens was born on November 9, 1988 in San Diego, California, USA as Alexandrea Kathryn Owens-Sarno. She is an actress known for Titanic (1997) and Background Music (2014). Alex had the amazing opportunity to play Cora Cartmell in Titanic when she was 8 years old. Now, she is pursuing an acting career.', 'San Diego, CA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (8553, '1989-10-30', null, null, 'Seth Adkins', 'From Wikipedia, the free encyclopedia.Seth Elijah Adkins (born October 30, 1989) is an American actor. He made his debut in the mid-1990s as a child artiste in the TV shows Small Talk and Sabrina, the Teenage Witch in 1996 and the films ...First Do No Harm and Titanic in 1997. He later made a successful transition to an adult performer. One of his most recent roles was in Matt Reeves'' Let Me In (2010).Description above from the Wikipedia article Seth Adkins, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Albuquerque, New Mexico, U.S.');
