INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (9257, '1949-04-28', '2006-08-14', null, 'Bruno Kirby', 'From Wikipedia, the free encyclopedia.Bruno Kirby (April 28, 1949 – August 14, 2006) was an American film and television actor. He was perhaps best known for his roles in the Hollywood films City Slickers, When Harry Met Sally..., Good Morning, Vietnam, and The Godfather Part II.Description above from the Wikipedia article Bruno Kirby, licensed under CC-BY-SA, full list of contributors on Wikipedia', 'New York City, New York, USA');
