INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (13022, '1949-05-31', null, null, 'Tom Berenger', 'Tom Berenger is an American television and motion picture actor. He was nominated for the Academy Award for Best Supporting Actor for his portrayal of Staff Sergeant Bob Barnes in Platoon. He is also known for playing Jake Taylor in the Major League films and Thomas Beckett in the Sniper films.​From Wikipedia, the free encyclopedia', 'Chicago, Illinois, USA');
