INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (14984, '1965-06-28', null, null, 'Jessica Hecht', 'Jessica Hecht is an American stage and screen actress and singer, best known for her recurring roles as Susan Bunch on the television show "Friends" and Gretchen Schwartz on "Breaking Bad". She holds an BA in Drama from the Tisch School of the Arts, New York University.', 'Princeton, New Jersey, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (15034, '1981-06-12', null, null, 'Jeremy Howard', 'From Wikipedia, the free encyclopedia.Jeremy Patrick Howard (born June 12, 1981) is an American actor. He appeared in the films Sydney White, Galaxy Quest, and Accepted.Howard was born in Burbank, California, the son of Sharon Hess and actor Joe Howard.Description above from the Wikipedia article Jeremy Howard, licensed under CC-BY-SA, full list of contributors on Wikipedia.​', 'Burbank, California, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (15091, '1977-05-16', null, null, 'Melanie Lynskey', 'Melanie Lynskey (born 16 May 1977) is a New Zealand actress best known for playing Charlie Harper''s neighbor/stalker Rose on Two and a Half Men, and a range of characters in films such as Win Win, Up in the Air, The Informant!, Away We Go, Flags of Our Fathers, Shattered Glass, Sweet Home Alabama, Ever After and Heavenly Creatures.', 'New Plymouth, New Zealand');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (15196, '1941-11-18', '2003-12-03', null, 'David Hemmings', 'David Edward Leslie Hemmings (18 November 1941 – 3 December 2003) was an English film, theatre and television actor as well as a film and television director and producer.He is noted for his role as the photographer in the drama mystery-thriller film Blowup (1966), directed by Michelangelo Antonioni. Early in his career, Hemmings was a boy soprano appearing in operatic roles. In his later acting career, he was known for his distinctive eyebrows and gravelly voice.Description above from the Wikipedia article David Hemmings, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Guildford, Surrey, England, UK');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (15234, '1971-09-08', null, null, 'David Arquette', 'From Wikipedia, the free encyclopedia.David Arquette (born September 8, 1971) is an American actor, film director, producer, screenwriter, fashion designer, and occasional professional wrestler. A member of the Arquette acting family, he first became known during the late 1990s after starring in several Hollywood films, including the Scream series. He has since had several television roles, including playing "Jason Ventress" on ABC''s In Case of Emergency. In addition to his acting career Arquette took a brief foray into professional wrestling in early 2000, competing for World Championship Wrestling (WCW). During his tenure, Arquette would become a one-time WCW World Heavyweight Champion, an angle which has been cited by prominent professional wrestling commentators as being pivotal to the degradation of the title and the demise of WCW.Description above from the Wikipedia article David Arquette, licensed under CC-BY-SA, full list of contributors on Wikipedia', 'Winchester, Virginia, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (15277, '1966-10-19', null, null, 'Jon Favreau', 'Jonathan Kolia Favreau (/ˈfævroʊ/; born October 19, 1966) is an American actor, director, producer and screenwriter.As an actor, Favreau has appeared in the films Rudy (1993), PCU (1994), Swingers (1996), Very Bad Things (1998), The Replacements (2000), Daredevil (2003), The Break-Up (2006), Couples Retreat (2009), I Love You, Man (2009), The Wolf of Wall Street (2013), and Chef (2014).As a filmmaker, Favreau has been significantly involved with the Marvel Comics franchise, particularly in collaboration with Robert Downey Jr. He directed, produced, and appeared as Happy Hogan in the films Iron Man (2008) and Iron Man 2 (2010). He also served as an executive producer for, and/or appeared as the character in, the films The Avengers (2012), Iron Man 3 (2013), Avengers: Age of Ultron (2015), Spider-Man: Homecoming (2017), Avengers: Infinity War (2018), Avengers: Endgame (2019), and Spider-Man: Far From Home (2019).As a director, he worked on the films Elf (2003), Zathura: A Space Adventure (2005), Cowboys & Aliens (2011), Chef (2014), The Jungle Book (2016) and The Lion King (2019). Favreau is the creator of the Star Wars Disney+ original series The Mandalorian (2019–present) as well as one of its executive producers and directors. He also serves as a director and executive producer for its spin-off series The Book of Boba Fett. He produces films under his production company banner, Fairview Entertainment, and also presents the television cooking series The Chef Show.Description above is from the Wikipedia article Jon Favreau, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Queens, New York, USA');
