INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (17417, '1976-05-12', null, null, 'Justin Shilton', 'Justin Shilton was born on May 12, 1976 in Los Angeles, California, USA as Justin Ari Shilton. He is an actor and writer, known for Little Miss Sunshine (2006), The Aviator (2004) and Orbit.', ' Los Angeles, California, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (17442, '1984-04-11', null, null, 'Kelli Garner', 'Kelli Brianne Garner is an American actress. Her credits include Man of the House, The Aviator, Bully and Thumbsucker. She appeared in two Green Day music videos "Jesus of Suburbia" and the unreleased "Whatsername". In December 2005, Garner starred in the Off-Broadway production of Dog Sees God: Confessions of a Teenage Blockhead at the Century Center for the Performing Arts.Description above from the Wikipedia article Kelli Garner, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Kern County, California, U.S.');
