INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (18023, '1961-04-14', null, null, 'Robert Carlyle', 'Robert Carlyle, OBE (born 14 April 1961) is a Scottish film and television actor. He is known for a variety of roles including those in Trainspotting, Hamish Macbeth, The Full Monty, The World Is Not Enough, Angela''s Ashes, The 51st State, and 28 Weeks Later. He also portrayed Doctor Nicholas Rush in Stargate Universe.', 'Glasgow, Scotland, UK');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (18082, '1968-05-20', null, null, 'Timothy Olyphant', 'Timothy David Olyphant (born May 20, 1968) is an American actor whose notable roles in television drama series include Deadwood as Seth Bullock, Justified as Raylan Givens, and Damages as Wes Krulik. He has also starred in the films Scream 2, Gone in 60 Seconds, Dreamcatcher, Live Free or Die Hard, A Perfect Getaway, The Crazies, and Hitman.Description above from the Wikipedia article Timothy Olyphant, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Honolulu, Hawaii, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (18236, '1950-02-13', null, null, 'Donna Hanover', '', 'Oakland, California, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (18269, '1968-12-03', null, '布兰登·费舍', 'Brendan Fraser', 'The son of a Canadian travel executive who frequently moved his family, Brendan can claim affinity with Ottawa, Indianapolis, Detroit, Seattle, London and Rome. His early exposure to theatre, particularly in London, led him to Seattle''s Cornish Institute. After graduation he found a minor role as Sailor #1 in River Phoenix''s Dogfight (1991), then somewhat more substantial roles in Encino Man (1992) and School Ties (1992). He expresses a preference for playing "fish out of water" men. Five more years of supporting work led finally to the title role in George of the Jungle (1997), a role which fully utilized his charm and beefy good looks, as well as offering him a chance to show off his comic talents. He describes this role as the one which dramatically altered his career. Critical raves for his role in Gods and Monsters (1998) pointed to yet another dimension to his dramatic persona.', 'Indianapolis, Indiana, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (18271, '1966-12-09', null, null, 'Toby Huss', 'Toby Edward Huss (born December 9, 1966) is an American actor. He is best known for portraying Artie, the Strongest Man in the World on the cult hit Nickelodeon TV series The Adventures of Pete &amp; Pete, for his voice-over work on the long running animated series King of the Hill, and for his role as Felix ''Stumpy'' Dreifuss on HBO''s Carnivàle.Huss was born in Marshalltown, Iowa. He attended the University of Iowa where he participated in No Shame Theatre before moving to Los Angeles to pursue an acting career.He has appeared in over 35 movies and television series. Notable roles include the voices of Cotton Hill and Kahn Souphanousinphone on King of the Hill and "The Wiz", a boyfriend of Elaine Benes in "The Junk Mail" episode of Seinfeld.Known for his parodies of Frank Sinatra, which were featured in the films Vegas Vacation and Down Periscope, Huss has created a Sinatra-inspired character named Rudy Casoni.In the early 1990s he appeared in network promos for MTV, playing odd characters, including a crooner known at the time as Ol'' Two Eyes (the predecessor to Rudy Casoni) who sang lounge-singer versions of Dr. Dre''s "Dre Day," Cypress Hill''s "Insane in the Brain," Pearl Jam''s "Jeremy," and Onyx''s hit "Slam"; a James Bond-like spy named Cobalt; a leather fetishist with an abnormal affection for goats; a flannel-wearing doofus; and an angry redneck the Reverend Tex Stoveheadbottom, who delivered fast-talking and descriptively detailed non-sequitir tirades that usually included the phrase, "Go to Hell!"Description above from the Wikipedia article Toby Huss, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Marshalltown, Iowa, U.S.');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (18328, '1944-03-28', '2016-03-23', null, 'Ken Howard', 'Ken Howard (1944–2016) was an American actor, best known for his roles in 1776 and on White Shadow. He was the National President of The Screen Actors Guild from 2009 until his death in 2016.', 'El Centro, California, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (18405, '1934-03-20', '2017-11-21', null, 'Peter Berling', '', 'Międzyrzecz, Poland');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (18471, '1970-08-15', null, null, 'Anthony Anderson', 'Anthony Anderson (born August 15, 1970) is an American actor, comedian, and writer. He has starred in his own sitcom All About the Andersons, as well as the Fox sitcom The Bernie Mac Show during the fifth and final season of the show. He is also known for his leading roles in television dramas such as K-Ville, The Shield and Law & Order. He has also had supporting roles in films such as Transformers, The Departed, Agent Cody Banks 2: Destination London and Scream 4.', 'Los Angeles, California, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (18472, '1969-03-27', null, null, 'Kevin Corrigan', 'Kevin Fitzgerald Corrigan is an American actor. He has appeared mostly in independent films and television since the 1990s, including the role of Uncle Eddie on the sitcom Grounded for Life. His film appearances have included a supporting role in The Departed and co-starring roles in Big Fan and Results. Wikipedia', 'The Bronx, New York, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (18473, '1978-05-01', null, null, 'James Badge Dale', 'James Badge Dale  (born May 1, 1978) is an American actor who starred in the AMC drama series Rubicon. He is most famous for his role of Chase Edmunds in the third season of 24 and Robert Leckie in the HBO miniseries The Pacific.Description above from the Wikipedia article James Badge Dale, licensed under CC-BY-SA, full list of contributors on Wikipedia', ' New York City, New York, USA');
