INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (19489, '1943-01-31', null, null, 'Peter McRobbie', 'Peter McRobbie is a Scottish-born American character actor, best known for his roles as Pop Pop Jamison in the 2015 horror film The Visit and Father Paul Lantom in Daredevil, as well as a recurring role in the TNT series The Alienist.', 'Hawick, Borders, Scotland, UK');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (19498, '1976-09-20', null, '乔·本恩瑟', 'Jon Bernthal', 'Jonathan E. "Jon" Bernthal (born September 20, 1976) is an American actor, best known for his role on the AMC television series The Walking Dead. He has performed in over 30 plays regionally and off-Broadway, including many with his own award-winning theatre company Fovea Floods. He also appeared in several TV shows such as Law & Order: Criminal Intent in 2002, The Class from 2006 to 2007 and The Pacific in early 2010.', 'Washington, District of Columbia, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (19511, '1945-03-03', null, null, 'Larry Pine', 'Larry Pine (born March 3, 1945) is an American film, television and theatre actor.He began his professional acting career Off-Broadway, then appeared in Cyrano de Bergerac at the Vivian Beaumont Theatre in 1968 as Fop. A founding member of the avant-garde theater company the Manhattan Project, Pine appeared with the group in Alice in Wonderland, directed by Andre Gregory, in 1970 (Manhattan Project 1973).He made his film debut in 1978 in James Ivory''s Hullabaloo Over Georgie and Bonnie''s Pictures, which was made for television, but later was released theatrically. Since then, he has performed in Louis Malle''s Vanya on 42nd Street, Woody Allen''s Celebrity, Small Time Crooks, Melinda and Melinda, and other films.He appears in the book Are You Dave Gorman? as the first actor encountered by the writer to have played a fictional Dave Gorman (in The Ice Storm). He has appeared twice as a "Charlie Rose type" interviewer in the films The Royal Tenenbaums and The Door in the Floor, featuring him in a dark studio conducting a one-on-one interview in Rose''s distinctive format.He appeared in All My Children as Max Jeffries (1992) and as Barry Shire #1 (1997–1999).Most recently, he appeared in Russ Emanuel''s "Chasing the Green" alongside Jeremy London, Ryan Hurst, William Devane, and Robert Picardo.He is married to composer and sound designer Margaret Pine.', 'Tucson, Arizona, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (19545, '1930-10-24', null, null, 'Jack Angel', 'Jack Angel was born on October 24, 1930 in Modesto, California, USA as Jack T. Angel. He is known for his work on A.I. Artificial Intelligence (2001), Toy Story (1995), and Treasure Planet (2002). He has been married to Arlene Thornton since 1984. They have three children. He was previously married to Barbara Champion.', 'Modesto, California');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (19654, '1963-08-30', null, null, 'Michael Chiklis', 'Michael Charles Chiklis (born August 30, 1963) is an American actor, voice actor, occasional director and television producer. Some of the previous roles for which he is best known include Commissioner Tony Scali on the ABC police drama The Commish, LAPD Detective Vic Mackey on the FX police drama The Shield, Thing in the Fantastic Four film series, and Jim Powell on the science-fiction, comedy-drama No Ordinary Family.', 'Lowell, Massachusetts, USA');
