INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (19866, '1973-04-10', null, null, 'Guillaume Canet', 'Guillaume Canet (born 10 April 1973) is a French actor and film director.Canet began his career in theatre and television before moving to film. He starred in several films like Joyeux Noël, Love Me If You Dare and The Beach. In 2006, he turned to writing and directing with Tell No One and won a César Award for Best Director.Canet has also received attention because of his personal life. He was formerly married to actress Diane Kruger. After they divorced he began a relationship with Marion Cotillard that was closely followed by the media.', 'Boulogne-Billancourt, Hauts-de-Seine, France');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (19867, '1964-01-23', null, null, 'Staffan Kihlbom', 'From Wikipedia, the free encyclopedia.Staffan Kihlbom (born 23 January 1964) is a Swedish actor, who appeared in the 2000 film The Beach.Description above from the Wikipedia article Staffan Kihlbom, licensed under CC-BY-SA, full list of contributors on Wikipedia.​', null);
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (19868, '1964-06-22', null, null, 'Paterson Joseph', 'Paterson Joseph (born 22 June 1964) is a British actor. He appeared in the Royal Shakespeare Company productions of King Lear and Love''s Labour''s Lost in 1990. On television he is known for his roles in Casualty, as Alan Johnson in Channel 4 sitcom Peep Show, Green Wing, Survivors, Boy Meets Girl, as DI Wes Layton in Law & Order: UK, and as Connor Mason in Timeless. His film roles include The Beach, Æon Flux and The Other Man.', 'London, England, U.K.');
