INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (30151, '1944-02-01', '2007-06-28', null, 'Leo Burmester', 'From Wikipedia, the free encyclopedia.  Leo Burmester (February 1, 1944 - June 28, 2007) was an American actor. Burmester worked for director John Sayles several times, including in Passion Fish (1992) and Lone Star (1996), and also for directors such as John Schlesinger and Sidney Lumet, and as the Apostle Nathaniel in Martin Scorsese''s The Last Temptation of Christ (1988).Description above from the Wikipedia article Leo Burmester, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Louisville, Kentucky, USA');
