INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (35219, '1955-08-03', null, null, 'Corey Burton', 'Corey Burton is an American voice actor known as the current voice of Ludwig Von Drake, Captain Hook and many other characters for numerous Disney projects, as well as several central characters for numerous Star Wars projects. He has worked on numerous cartoon series for major networks such as Cartoon Network and has worked extensively with The Walt Disney Company and Disney theme parks.', 'Los Angeles - California - USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (35468, '1977-01-08', null, null, 'Amber Benson', '​From Wikipedia, the free encyclopedia.Amber Nicole Benson  (born January 8, 1977) is an American actress, writer, film director, and film producer. She is best known for her role as Tara Maclay on the TV series Buffy the Vampire Slayer, but has also directed, produced and starred in her own films Chance (2002) and Lovers, Liars &amp; Lunatics (2006).She is also co-director with Adam Busch of Drones (2010).Description above from the Wikipedia article Amber Benson, licensed under CC-BY-SA, full list of contributors on Wikipedia', 'Birmingham - Alabama - USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (35476, '1976-03-17', null, null, 'Brittany Daniel', 'From Wikipedia, the free encyclopedia.Brittany Ann Daniel (born March 17, 1976, height 5'' 7" (1,70 m)) is an American television and film actress. She is the twin sister of actress and photographer Cynthia Daniel. She is best known for her portrayal of Kelly Pitts on the CW/BET comedy-drama series The Game, and Candice in the 2010 science fiction film Skyline, and for her role as Jessica Wakefield on the 1990''s teen show Sweet Valley High.Description above from the Wikipedia article Brittany Daniel, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Gainesville - Florida - USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (35530, '2018-05-01', null, null, 'Christopher Thompson', '', 'Grimsby, England, UK');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (35780, '1942-10-11', null, null, 'Amitabh Bachchan', 'Amitabh Bachchan, is an Indian film actor. He first gained popularity in the early 1970s as the "angry young man" of Hindi cinema, and has since become one of the most prominent figures in the history of Indian cinema.  Bachchan has won numerous major awards in his career, including four National Film Awards, three of which are in the Best Actor category, and fourteen Filmfare Awards. He is the most-nominated performer in any major acting category at Filmfare, with 36 nominations overall. In addition to acting, Bachchan has worked as a playback singer, film producer and television presenter, and was an elected member of the Indian Parliament from 1984 to 1987.', 'Allahabad, Uttar Pradesh, India.');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (36041, '1956-08-01', null, null, 'Lewis Smith', 'From Wikipedia, the free encyclopediaLewis Smith (born August 1, 1956), is an American actor.Description above from the Wikipedia article Lewis Smith, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Chattanooga, Tennessee, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (36169, '1951-07-12', null, null, 'Cheryl Ladd', 'Cheryl Ladd  (born Cheryl Jean Stoppelmoor; July 12, 1951) is an American actress, singer and author. Ladd is best known for her role as Kris Munroe in the television series Charlie''s Angels, hired amid a swirl of publicity prior to its second season in 1977 to replace the departing Farrah Fawcett-Majors. Ladd remained with the show until its cancellation in 1981.Description above from the Wikipedia article Cheryl Ladd, licensed under CC-BY-SA, full list of contributors on Wikipedia', 'Huron - South Dakota - USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (36662, '1985-05-28', null, null, 'Carey Mulligan', 'Carey Hannah Mulligan (born 28 May 1985) is an English actress. She made her film debut as Kitty Bennet in Pride & Prejudice (2005). Following Pride & Prejudice, she had roles in numerous British programs, including Doctor Who as Sally Sparrow. In 2007, made her Broadway debut in The Seagull to critical acclaim. In 2009, she gained widespread recognition for playing the lead role of Jenny in An Education, winning a BAFTA Award for Best Actress in a Leading Role. Additionally, Mulligan was nominated for a Golden Globe Award and an Academy Award for her performance. Following her breakthrough role as Jenny. Description above from the Wikipedia article Carey Mulligan, licensed under CC-BY-SA, full list of contributors on Wikipedia​', ' Westminster, London, England');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (36801, '1973-04-03', null, null, 'Adam Scott', 'Adam Scott (born April 3, 1973) is an American actor, producer, screenwriter, director and podcaster. He is best known for his roles as Ben Wyatt in the NBC comedy Parks and Recreation, Henry Pollard in the Starz comedy series Party Down, Palek in the HBO drama Tell Me You Love Me and Derek Huff in the 2008 film Step Brothers. He has appeared in the Adult Swim comedy Childrens Hospital. He was also on the show NTSF:SD:SUV::, and the annual Adult Swim special The Greatest Event in Television History, which he also created and produced. Scott is the co-host and co-creator of the Earwolf podcast U Talkin'' U2 To Me? with Scott Aukerman.', 'Santa Cruz - California - USA');
