INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (40900, '1960-01-18', null, null, 'Mark Rylance', 'Sir David Mark Rylance Waters (born 18 January 1960 in Ashford, Kent, England) is an English actor, theatre director, and playwright.He was the first artistic director of Shakespeare''s Globe in London, between 1995 and 2005. His film appearances include Prospero''s Books (1991), Angels and Insects (1995), Institute Benjamenta (1996), and Intimacy (2001). Rylance won the Academy Award and BAFTA Award for Best Supporting Actor for his portrayal of Rudolf Abel in Bridge of Spies (2015). Rylance played the title role in Steven Spielberg''s The BFG (2016), a live-action film adaptation of the children''s book by Roald Dahl, and appeared in Christopher Nolan''s Dunkirk (2017), based on the British evacuation in World War II. He appeared as James Halliday in Spielberg''s 2018 film Ready Player One, based on the novel of the same name.', 'Ashford, Kent, UK');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (40938, '1973-08-14', null, null, 'Romane Bohringer', 'From Wikipedia, the free encyclopediaRomane Bohringer (born 14 August 1973 in Pont-Sainte-Maxence, Oise, France) is a French actress, film director, screenwriter and costume designer. She is the daughter of Richard Bohringer and sister of Lou Bohringer. Her parents named her after Roman Polanski.She won the César Award for Most Promising Actress for her role in Savage Nights.Description above from the Wikipedia article Romane Bohringer, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Pont-Sainte-Maxence, Oise, France');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (41266, '1954-09-08', null, null, 'Mark Lindsay Chapman', '​From Wikipedia, the free encyclopedia.  Mark Lindsay Chapman (born 8 September 1954 in London) is an English film and television actor.His credits include: Max Headroom, Dallas (as Brett Lomax), Falcon Crest (as Charley St. James), Baywatch, Murder She Wrote, Lois and Clark, JAG, Charmed, Roland Burke in The Young and the Restless, Dr. Anton Arcane (1990–1993) in Swamp Thing, and Nick Hopewell in The Langoliers.Description above from the Wikipedia articleMark Lindsay Chapman  licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'London, England, UK');
