INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (52995, '1943-07-21', '2014-12-31', null, 'Edward Herrmann', 'Edward Kirk Herrmann (July 21, 1943 – December 31, 2014) in Washington, D.C., was an American television and film actor. He is best known for his Emmy-nominated portrayals of Franklin D. Roosevelt on television, to younger generations for his role as Richard Gilmore in Gilmore Girls, as a ubiquitous narrator for historical programs on the History Channel, and as the spokesperson for Dodge automobiles in the 1990s.Hermann died from brain cancer, Wednesday, December 31, 2014, in a hospital in New York City.', 'Washington, District of Columbia, USA');
