INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (53807, '1986-08-28', null, null, 'Armie Hammer', 'Armand Douglas "Armie" Hammer (born August 28, 1986) is an American actor. After appearing on television and playing the title role in 2008''s Billy: The Early Years, he became known for his portrayal of the Winklevoss twins in the 2010 film The Social Network. His notorious film roles include Clyde Tolson in J. Edgar, Prince Albert Alcott in Tarsem Singh''s Snow White, and The Lone Ranger.', 'Los Angeles, California, USA');
