INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (55254, '1967-08-30', null, null, 'Frederique van der Wal', 'Frederique van der Wal est un mannequin néerlandais né le 30 août 1967 à La Haye (Pays-Bas). Elle est apparue à la télévision et au cinéma (notamment dans Wild Wild West en 1999).Frederique van der Wal (also Frédérique van der Wal (born August 30, 1967) is a Dutch model, business woman and entrepreneur best known for her appearances in Victoria''s Secret, and the covers of such magazines as Cosmopolitan, and Vogue. Van der Wal grew up in The Hague.', 'The Hague, Zuid-Holland, Netherlands');
