INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (58000, '1964-11-30', null, null, 'Sasha Jenson', 'Sasha Jenson is an American film and television actor known for his role in the 1993 hit film Dazed and Confused. He is also known for his role in the 1988 horror movie Halloween 4: The Return of Michael Myers, and for having a role in the 1992 hit movie Buffy the Vampire Slayer. His most recent film is in the 2003 release Grind. Jenson starred in the television series Teen Angel as Jason, and made guest appearances on shows like NYPD Blue and Monsters.Jenson is the son of Canadian-born actor and stuntman Roy Jenson. He is best known for being the coolest dude in Dazed and Confused according to everyone I, the editor of this bio, has ever watched the film with. Early in his career, he primarily did stunt work.Description above from the Wikipedia article Sasha Jenson, licensed under CC-BY-SA,full list of contributors on Wikipedia.', 'Los Angeles, California, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (58098, '1954-01-12', null, null, 'Howard Stern', 'Howard Allan Stern (born January 12, 1954)[1] is an American radio and television personality, comedian, and author. He is best known for his radio show, The Howard Stern Show, which gained popularity when it was nationally syndicated on terrestrial radio from 1986 to 2005. Stern has broadcast on Sirius XM Satellite Radio since 2006.', 'Jackson Heights, Queens, New York City, New York, USA');
