INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (66441, '1985-09-26', null, null, 'Talulah Riley', 'Talulah Riley is a British stage, film and television actress and novelist, best known for playing recurring character Angela on the HBO television series _Westworld_.', 'Hertfordshire, England, UK');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (66620, '1954-07-17', null, null, 'Angela Merkel', 'Angela Dorothea Merkel (née Kasner; born 17 July 1954) is a German politician who has been serving as the chancellor of Germany since 2005. She served as the leader of the Opposition from 2002 to 2005 and as the leader of the Christian Democratic Union (CDU) from 2000 to 2018. A member of the CDU, Merkel is the first female chancellor of Germany. During her tenure as Chancellor, Merkel has been frequently referred to as the de facto leader of the European Union and the most powerful woman in the world. ...Source: Article "Angela Merkel" from Wikipedia in english, licensed under CC-BY-SA 3.0.', 'Hamburg, Germany');
