INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (76245, '1964-05-27', null, null, 'Adam Carolla', 'From Wikipedia, the free encyclopedia.Adam Carolla (born May 27, 1964) is an American radio personality, television host, comedian, and actor. He currently hosts The Adam Carolla Show, a talk show distributed as a podcast on the ACE Broadcasting Network.Carolla is also known as being the co-host of the radio show Loveline from 1995 to 2005 (and its television incarnation on MTV from 1996 to 2000), as the co-host of the television program The Man Show (1999–2004), and as the co-creator and performer on the television program Crank Yankers (2002–2007).In November 2010 Carolla''s book In Fifty Years We''ll All Be Chicks debuted on the New York Times Best Seller List for hardcover non-fiction.Description above from the Wikipedia article Adam Carolla, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Los Angeles, California, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (76512, '1969-07-17', null, null, 'Jason Clarke', 'An Australian film and television actor. Clarke has appeared in many TV series. He is best known as a TV actor for his role as Tommy Caffee on the television series Brotherhood. He has also appeared in many films, often as an antagonist.', 'Winton, Queensland, Australia');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (76760, '1972-09-30', null, null, 'Meadow Sisto', 'Meadow Sisto is an American actress mostly known for playing Caroline in the 1992 film Captain Ron. Sisto is the daughter of Dick Sisto, a jazz vibist and Reedy Gibbs, an actress. Her younger brother, Jeremy Sisto, is also an actor. She lives in Portland, Oregon. Wikipedia', null);
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (76827, '1973-02-25', null, null, 'Hélène de Fougerolles', 'Hélène Christine Rigoine de Fougerolles (French pronunciation: [elɛn də fuʒʁɔl]; born February 25, 1973) is a French actress. She is the daughter of Alain Rigoine de Fougerolles and Anne Saumay de Laval. Initially planning to become a beautician, she began to study acting at age 15. She later attended acting classes in Paris and briefly at the Actors Studio in New York City.Fougerolles was married to Éric Hubert. They have a daughter Shana born in 2003.Description above from the Wikipedia article Hélène de Fougerolles, licensed under CC-BY-SA, full list of contributors on Wikipedia', 'Vannes, France');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (77002, null, null, null, 'Ashley Springer', 'From Wikipedia, the free encyclopediaAshley Springer is an American actor based in New York City whose screen credits include the 2007 horror-black comedy film, Teeth, written and directed by Mitchell Lichtenstein, and the 2009 drama Dare, directed by Adam Salky.Springer, a native of Manhattan, attended Stuyvesant High School, and went on to graduate from the Tisch School of the Arts at New York University in 2004.On stage he has appeared in the 2007 off-off-Broadway production of Sweet Love Adieu. His other films include Assassination of a High School President, The Secret), Anamorph, The Visitor, and What Are the Odds?.Description above from the Wikipedia article Charles McCallum, licensed under CC-BY-SA, full list of contributors on Wikipedia.', null);
