INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (80602, '1969-09-13', null, null, 'Tyler Perry', 'Tyler Perry (born Emmitt Perry, Jr.; September 13, 1969) is an African-American actor, director, playwright, screenwriter, producer, author, and songwriter. Already a successful artist in Southern theater, Perry began to make national celebrity status in 2005 with the release of his first film, Diary of a Mad Black Woman. In 2009, Perry was ranked by Forbes magazine as the sixth highest-paid man in Hollywood. He has won critical praise for playing Madea, a mentally strong woman who handles adversity with optimism and happiness; his critically acclaimed portrayal of her in the award-winning "Madea" series has caused him to be one of the most respected black actors in Hollywood. As of July 2009, Perry''s films had grossed nearly $400 million worldwide.', 'New Orleans, Louisiana, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (80978, '1963-01-01', null, null, 'Brian Goodman', 'From Wikipedia, the free encyclopedia.Brian Goodman is an American film and television director, writer, and actor.', 'U.S.A');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (81133, '1976-12-05', null, null, 'Amy Acker', 'Born and raised in Dallas, Texas, Amy Acker is the oldest of four children, three daughters and one son, of Robert Acker, a lawyer, and Sandra Bruyere, a housewife, and the cousin of Joel Jeffrey Acker. She graduated from Lake Highlands High School in Dallas. She subsequently earned a bachelor''s degree in theater from Southern Methodist University. In her junior year of college, Acker modeled for J. Crew catalog. In 1999 she was nominated for a Leon Rabin award for "Outstanding Performance by an Actress in a Lead Role" for her performance in the play Thérèse Raquin. She received a Bachelor of Fine Arts degree in the same year. She worked as a stage actress for several seasons, including a stint at American Players Theatre in Spring Green, Wisconsin.She appeared in various roles during the fantasy segments for the popular award-winning children''s TV series "Wishbone" (1995), which was filmed in Texas and consisted of Dallas theater actors. Upon graduation she worked in Wisconsin and New York before winning the role of "Fred" on "Angel" (1999). She won the 2003 Saturn Award for Best Supporting Actress on Television for her portrayal.', ' Dallas, Texas, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (81217, '1980-02-12', null, null, 'Sarah Lancaster', '​From Wikipedia, the free encyclopediaSarah Lancaster (born February 12, 1980) is an American actress. She is known for her roles of Rachel in Saved by the Bell: The New Class and Madison Kellner on Everwood. She also had a recurring guest role on NBC''s Scrubs as JD''s love-interest, Lisa the Gift-Shop Girl, and played Marjorie in ABC''s TV series What About Brian. In 2005 she starred in the TV movie Living With the Enemy with Mark Humphrey. Lancaster currently co-stars in the NBC comedy-spy series Chuck, as the title character''s sister, Ellie Bartowski and has done so since the show''s inception.Description above from the Wikipedia article Sarah Lancaster, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Overland Park, Kansas, U.S.A.');
