INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (84497, '1979-08-27', null, null, 'Aaron Paul', 'Aaron Paul Sturtevant (born August 27, 1979) is an American actor. He is best known for his critically acclaimed role as Jesse Pinkman in the hit AMC series Breaking Bad (2008–13). For his portrayal of Jesse Pinkman, he won the Primetime Emmy Award for Outstanding Supporting Actor in a Drama Series in 2010 and 2012, making him one of five actors to win this award more than once. He also won the Saturn Award for Best Supporting Actor on Television in 2010, 2012, and 2014, making him the only actor to win this award three times.​From Wikipedia, the free encyclopedia', 'Emmett, Idaho, USA');
