INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (93527, '1981-02-11', null, null, 'Kelly Rowland', 'Kelendria Trene "Kelly" Rowland (born February 11, 1981) is an American singer,songwriter, actress and television personality. Rowland rose to fame in the late 1990s as a member of Destiny''s Child, one of the world''s best-selling girl groups of all time. During their hiatus, Rowland released her debut solo album Simply Deep (2002), which sold 2.5 million copies worldwide[2] and produced the number-one single "Dilemma" with Nelly, as well as the international top-ten hit "Stole". Rowland also transitioned into acting, with guest appearances in television sitcoms, and starring roles in Freddy vs. Jason (2003) and The Seat Filler (2004).', 'Atlanta - Georgia - USA');
