INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (97446, '1965-08-12', null, null, 'James DuMont', '​From Wikipedia, the free encyclopediaJames DuMont (born August 12, 1965) is an American character actor and producer. DuMont''s career includes a lead performance in the political thriller Enemies Among Us, opposite Eric Roberts and Billy Zane. DuMont has appeared in many TV shows, such as Numb3rs, House, and more recently, Treme. Major films DuMont has acted in are Catch Me if You Can, S.W.A.T., and Speed.Description above from the Wikipedia article James DuMont, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Chicago, Illinois, USA');
