INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (113231, '1973-08-26', null, null, 'Gunner Wright', 'Gunner Wright is an American film actor known for his role in the film Love and for "portraying" the face and voice of Isaac Clarke in the videogame Dead Space 2. Wright raced motorcycles competitively until the age of 21 when he moved to Southern California. There he began working on Fox Television''s Fastlane series and soon began a career in acting. He appeared in director Clint Eastwood''s J. Edgar film.', 'Eustis, Florida');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (113460, '1943-12-11', null, null, 'John Kerry', 'An American politician and diplomat, currently serving as the first United States special presidential envoy for climate. He previously served as the 68th United States secretary of state from 2013 to 2017. An attorney and former naval officer, Kerry first drew public attention as a decorated Vietnam veteran turned anti-war activist. He went on to serve as a prosecutor and as Lieutenant Governor of Massachusetts, before serving as United States Senator from Massachusetts from 1985 to 2013. A member of the Democratic Party, he was the Democratic nominee for President of the United States in the 2004 election, which he lost to incumbent President George W. Bush.', 'Aurora, Colorado, USA');
