INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (449538, '1988-05-05', null, null, 'Adele', 'Adele Laurie Blue Adkins MBE (born 5 May 1988) is an English singer and songwriter. Graduating from the BRIT School for Performing Arts and Technology in 2006, Adele was given a recording contract by XL Recordings after a friend posted her demo on Myspace the same year. In 2007, she received the Brit Awards "Critics'' Choice" award and won the BBC Sound of 2008 poll. Her debut album, 19, was released in 2008 to commercial and critical success. It is certified seven times platinum in the UK, and double platinum in the US. An appearance she made on Saturday Night Live in late 2008 boosted her career in the US. At the 51st Annual Grammy Awards in 2009, Adele received the awards for Best New Artist and Best Female Pop Vocal Performance.Description above from the Wikipedia article Adele, licensed under CC-BY-SA, full list of contributors on Wikipedia.', 'Tottenham, London, England, UK');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (481337, '1996-12-21', null, null, 'Kaitlyn Dever', 'Kaitlyn Rochelle Dever (born December 21, 1996) is an American actress. She has appeared in several television series, including Justified (2011–2015) and Last Man Standing (2011–present), and starred in the Netflix miniseries Unbelievable (2019) for which she earned a Golden Globe Award nomination for Best Actress – Miniseries. In film, Dever has taken on supporting parts in Bad Teacher (2011), The Spectacular Now (2013), Short Term 12 (2013), Detroit (2017), and Beautiful Boy (2018). She has also played leading roles in the 2019 films Them That Follow and Booksmart.', 'Phoenix, Arizona, USA');
INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (484359, '1984-01-30', null, null, 'Kid Cudi', 'Scott Ramon Seguro Mescudi (born January 30, 1984),better known by his stage name Kid Cudi (/ˈkʌdi/ often stylized KiD CuDi), is an American singer, rapper, songwriter, record producer and actor from Cleveland, Ohio.As of 2018, Cudi has released six studio albums as a solo artist and has sold over 5.2 million digital singles and has worked with numerous prominent artists in the music industry, including Jay Z, Mary J. Blige, David Guetta, MGMT, Shakira, Snoop Dogg and Michael Bolton among others', 'Shaker Heights, Ohio, USA');
