INSERT INTO MovieKnowledge.person (person_id, person_birth_day, person_death_day, person_name, person_english_name, person_biography, person_birth_place) VALUES (1060565, '1946-11-30', null, null, 'Marina Abramović', 'Marina Abramović (born November 30, 1946 in Belgrade) is a New York-based Serbian performance artist who began her career in the early 1970s. Active for over three decades, she has recently begun to describe herself as the "grandmother of performance art". Abramović''s work explores the relationship between performer and audience, the limits of the body, and the possibilities of the mind.', 'Belgrade, Serbia, Yugoslavia');
