create table person
(
    person_id           int           not null
        primary key,
    person_birth_day    varchar(45)   null,
    person_death_day    varchar(45)   null,
    person_name         varchar(45)   null,
    person_english_name varchar(45)   null,
    person_biography    varchar(5000) null,
    person_birth_place  varchar(100)  null
);

